import UIKit
import TwitterKit

protocol LoginViewControllerInput {
    
}

protocol LoginViewControllerOutput {
    
}

class LoginViewController: UIViewController, LoginViewControllerInput {
    
    var output: LoginViewControllerOutput?
    var router: LoginRouter?
    
    // MARK: Object lifecycle
    
    override func awakeFromNib() {
        super.awakeFromNib()
        LoginConfigurator.sharedInstance.configure(viewController: self)
    }
    
    // MARK: View lifecycle
    
    override func viewWillDisappear(_ animated: Bool) {
        super.viewWillDisappear(animated)
        
    }
    
    override func viewDidLoad() {
        super.viewDidLoad()
        
        let store = TWTRTwitter.sharedInstance().sessionStore //This must be in TwitterAPIStore
        
        if let _ = store.session()?.userID {
            self.router?.navigateToTweetsTimelineScene()
        }
        else {
            TWTRTwitter.sharedInstance().logIn(completion: { (session, error) in
                if (session != nil) {
                    self.router?.navigateToTweetsTimelineScene()
                } else {
                    let alert = UIAlertController(title: "Error", message: "Algo ha ido mal, pruebas m√°s tarde", preferredStyle: UIAlertControllerStyle.alert)
                    alert.addAction(UIAlertAction(title: "Aceptar", style: .default, handler: { action in
                        switch action.style{
                        case .default:
                            print("default")
                        case .cancel:
                            print("cancel")
                        case .destructive:
                            print("destructive")
                        }}))
                    self.present(alert, animated: true, completion: nil)
                }
            })
        }

    }
    
    // MARK: Requests
    
    
    // MARK: Display logic
    
}

//This should be on configurator but for some reason storyboard doesn't detect ViewController's name if placed there
extension LoginViewController: LoginPresenterOutput {
    override func prepare(for segue: UIStoryboardSegue, sender: Any?) {
        router?.passDataToNextScene(for: segue)
    }
}
